import { Component, Input } from '@angular/core';
import { Http, Response, Headers, RequestOptions } from '@angular/http';

import { NavParams, ViewController } from 'ionic-angular';

import { IClient } from '../../../core/client/client';

import { Utils } from '../../../providers/utils';
import { BillStore } from '../../../core/bill/bill-store';
import { WorkoutStore } from '../../../core/workout/workout-store';

@Component({
  templateUrl: 'client-detail-billing.html'
})
export class ClientDetailBillingModal {
  @Input() client: IClient;
  editing: boolean = false;

  constructor(
    private params: NavParams,
    private viewCtrl: ViewController,
    private http: Http,
    private utils: Utils,
    private billStore: BillStore,
    private workoutStore: WorkoutStore,
  ) {
    this.params = params;
    this.viewCtrl = viewCtrl;
    this.http = http;
    this.utils = utils;

    this.billStore = billStore;
    this.workoutStore = workoutStore;

    this.trainingPrice = 120;
  }

  ngOnInit(): void {
    if (this.params.data.hasOwnProperty('key')) {
      this.editing = true;
      this.client = this.params.data;
    } else {
      this.client = {};
    }

    // this.client.trainingsTodoCount = 0;
    // this.client.trainingsDoneCount = 0;
    // this.client.payed = 0;
    // this.client.trainingsMovedCount = 0;
    // this.client.surcharge = 0;
    this.client.surcharge = 0;
    this.client.total = 0;

    this.bill = {};
  }

  ionViewDidEnter(): void {
    this.client.month = '2016-11';
    this.updateMonth();
  }

  updateMonth(): void {
    this.bill = this.billStore.getItemByClientMonth(this.client.key, this.client.month);
    if (!this.bill) {
      this.billStore.createBill(this.client.key, this.client.month).then(() => {
        this.bill = this.billStore.getItemByClientMonth(this.client.key, this.client.month);
      });
      this.client.payed = 0;
      this.client.discount = 0;
    } else {
      this.client.payed = this.bill.payed;
      this.client.discount = this.bill.discount;
    }

    // temporary use
    if (this.client.name.slice(-1) === 'a') {
      this.client.gender = 'k';
    } else {
      this.client.gender = 'm';
    }

    let date = new Date(this.client.month + '-01');
    let year = date.getFullYear();
    let month = date.getMonth();
    let firstDay = new Date(year, month, 1);
    let lastDay = new Date(year, month + 1, 1);
    this.trainingsTodo = this.workoutStore.filterBy({
      client: this.client.key,
      fixed: false,
      dateAfter: firstDay,
      dateBefore: lastDay
    });
    this.client.trainingsTodoCount = this.trainingsTodo.size;

    firstDay = new Date(year, --month, 1);
    lastDay = new Date(year, month + 1, 1);
    this.trainingsDone = this.workoutStore.filterBy({
      client: this.client.key,
      fixed: false,
      dateAfter: firstDay,
      dateBefore: lastDay
    });
    this.client.trainingsDoneCount = this.trainingsDone.size;

    this.updateAll();
  }

  updateAll(): void {
    this.client.trainingsMovedCount = (this.client.payed - this.client.trainingsDoneCount) || '0';
    this.updateTrainings();
  }

  updateTrainings(): void {
    if (this.client.trainingsMovedCount < 0) {
      this.client.trainingsSurcharge = this.client.trainingsMovedCount * -this.trainingPrice;
      this.client.trainingsMovedCount = 0;
    } else {
      this.client.trainingsSurcharge = 0;
    }
    this.trainings = this.client.trainingsTodoCount - this.client.trainingsMovedCount;
    this.updateDiscount();
  }

  updateDiscount(): void {
    if (!this.client.trainingsSurcharge) {
      this.client.trainingsSurcharge = 0;
    }
    this.subtotal = this.trainings * this.trainingPrice + parseFloat(this.client.trainingsSurcharge);
    if (this.subtotal > 0) {
      this.client.discountAmount = parseFloat(this.client.discount > 0 ? (this.subtotal * this.client.discount / 100) || 0 : 0).toFixed(2);
    } else {
      this.client.discountAmount = 0;
    }
    this.updateTotal();
  }

  updateTotal(): void {
    if (!this.client.discountAmount) {
      this.client.discountAmount = 0;
    }
    if (!this.client.surcharge) {
      this.client.surcharge = 0;
    }
    this.client.total = parseFloat((this.subtotal - parseFloat(this.client.discountAmount) + parseFloat(this.client.surcharge)) || 0).toFixed(2);
  }

  generatePDF(): void {
    this.updateTotal();
    let surchargeBill = '';
    if (this.client.trainingsSurcharge > 0) {
      surchargeBill += parseFloat(this.client.trainingsSurcharge || 0).toFixed(2) + ' zł';
      if (this.client.surcharge) {
        surchargeBill += ' + ';
      }
    }
    if (this.client.surcharge > 0) {
      surchargeBill += parseFloat(this.client.surcharge || 0).toFixed(2) + ' zł';
    }
    if (this.client.surchargeDesc) {
      surchargeBill += ' (' + this.client.surchargeDesc + ')';
    }

    let dd = {
      content: [
        {
          image: 'logo',
          width: 300,
          alignment: 'center'
        },
        {
          style: 'content',
          margin: [15, 0],
          text: [
            (this.client.gender === 'k' ? 'Szanowna Pani' : 'Szanowny Pan') + ', \r\n',
            { text: this.client.lastname + ' ' + this.client.name, style: 'field' },
          ]
        },
        {
          style: 'content',
          margin: [0, 20],
          ul: [
            {
              text: [
                'Liczba treningów zaplanowana w miesiącu listopad: ',
                { text: parseInt(this.client.trainingsTodoCount || 0) + '', style: 'field' }
              ]
            },{
              text: [
                'Liczba treningów przechodzących z ubiegłego miesiąca: ',
                { text: parseInt(this.client.trainingsMovedCount || 0) + '', style: 'field' }
              ]
            },{
              text: [
                'Dopłata z ubiegłego miesiąca: ',
                { text: surchargeBill, style: 'field' }
              ]
            },{
              text: [
                'Rabat o wartości: ',
                { text: parseFloat(this.client.discountAmount || 0).toFixed(2) + ' zł', style: 'field' }
              ]
            }
          ]
        },
        {
          style: 'content',
          margin: [15, 0],
          text: [
            'Razem do zapłaty: ',
            { text: parseFloat(this.client.total || 0).toFixed(2) + ' zł', style: 'field' }
          ]
        },
        {
          margin: [0, 50],
          canvas: [
            {
              type: 'line',
              x1: 0,
              y1: 5,
              x2: 535,
              y2: 5,
              lineWidth: 0.5
            }
          ]
        },
        {
          style: 'content',
          columns: [[
              'Płatność gotówką lub przelewem: \r\n',
              { text: 'TRENINGI PERSONALNE \r\nLISTOPAD', bold: true },
              { text: '\r\n\r\nTermin płatności do 5-go każdego miesiąca.', style: 'small' }
            ],[
              {
                bold: true,
                text: [
                  'Egobody Jarosław Jakubowski\r\n',
                  'ul. Trawowa 27/2\r\n',
                  '54-614 Wrocław\r\n'
                ]
              },
              '\r\nNr rachunku:',
              { text: '63 1090 2398 0000 0001 1658 5270', bold: true }
          ]]
        }
      ],
      images: {
        logo: 'data:image/jpeg;base64,',
      },
      styles: {
        content: {
          fontSize: 15
        },
        small: {
          fontSize: 12
        },
        field: {
          bold: true,
          background: '#ccc'
        }
      }
    };
    return pdfMake.createPdf(dd);
  }

  preview(): void {
    this.generatePDF().open(this.client.month + ' - ' + this.client.lastname + ' ' + this.client.name + '.pdf');
  }

  send(): void {
    this.client.billSent = true;
    this.generatePDF().getBase64((pdf) => {
      let body = JSON.stringify({
        email: this.client.email,
        name: this.client.name + ' ' + this.client.lastname,
        file: pdf,
        fileName: this.client.month + ' - ' + this.client.lastname + ' ' + this.client.name + '.pdf'
      });
      let headers = new Headers({ 'Content-Type': 'application/json' });
      let options = new RequestOptions({ headers: headers });
      this.http.post('http://treningi.egobody.pl/bill.php', body, options)
        .toPromise()
        .then((res: Response) => {
          let result = res.json() || {};
          console.log(result);
          this.client.billSendStatus = 'Rachunek został wysłany do klienta na adres:';
        })
        .catch((error: any) => {
          // In a real world app, we might use a remote logging infrastructure
          // We'd also dig deeper into the error to get a better message
          let errMsg = (error.message) ? error.message :
            error.status ? `${error.status} - ${error.statusText}` : 'Server error';
          console.error(errMsg); // log to console instead
          this.client.billSendError = 'Wystąpił błąd podczas wysyłania rachunku na adres:';
          this.client.billSent = false;
          return Promise.reject(errMsg);
        });
    });
    // pdf.open(this.client.month + ' - ' + this.client.lastname + ' ' + this.client.name + '.pdf');
  }

  save(): void {
    this.billStore.updateBill(this.bill, {
      payed: parseFloat(this.client.payed) || 0,
      discount: parseFloat(this.client.discount)
    });
    this.viewCtrl.dismiss(this.client);
  }

  dismiss(): void {
    this.viewCtrl.dismiss();
  }

  delete(): void {
    this.client.delete = true;
    this.viewCtrl.dismiss(this.client);
  }
}
